package com.kpit.selenium.master.sequential;

import java.io.BufferedWriter;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.FileWriter;
import java.io.IOException;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Properties;

import javax.mail.MessagingException;

import org.apache.log4j.Logger;
import org.apache.log4j.PropertyConfigurator;
import org.apache.poi.hssf.usermodel.HSSFCell;
import org.apache.poi.hssf.usermodel.HSSFRow;
import org.apache.poi.hssf.usermodel.HSSFSheet;
import org.apache.poi.hssf.usermodel.HSSFWorkbook;
import org.junit.runner.JUnitCore;

import com.kpit.selenium.suite.datatable.Xls_Reader;
import com.kpit.selenium.suite.reports.ReportUtil;
import com.kpit.selenium.suite.testscript.DriverScript;
import com.kpit.selenium.suite.util.Constants;
import com.kpit.selenium.suite.util.SendMail;
import com.kpit.selenium.suite.util.TestUtil;


/**
 * @author milindw3
 */
public class SequentialTestSuiteRunner {
    private static final Logger LOG = Logger.getLogger(SequentialTestSuiteRunner.class);


    public static void main(String[] args) {
    	System.setProperty("jsse.enableSNIExtension", "false");
        PropertyConfigurator.configure(System.getProperty("user.dir") + File.separatorChar + "src" + File.separatorChar
                        + "log4j.properties");

        if (LOG.isDebugEnabled()) {
            LOG.debug("SequentialTestSuitRunner.main - start");
        }
        try {
            Properties CONFIG = new Properties();
            String targetEnvironment = System.getProperty("targetEnvironment");
            String targetBuildName = System.getProperty("targetBuildName");
            System.out.println("targetEnvironment >> " + targetEnvironment + " >> targetBuildName >> " + targetBuildName);
           
       
            if (LOG.isDebugEnabled()) {
                LOG.debug("SequentialTestSuitRunner.main - targetEnvironment " + targetEnvironment);
            }
            FileInputStream fs = null;
            if(null != targetEnvironment && !"".equals(targetEnvironment)){
                String envtVariable = System.getProperty("user.dir") + File.separatorChar + targetEnvironment + ".properties";
                System.out.println("envtVariable >> " + envtVariable);
				fs = new FileInputStream(envtVariable);
            } else {
            	fs = new FileInputStream(System.getProperty("user.dir") + File.separatorChar + "config"
                        + File.separatorChar + "config.properties");
            }

            CONFIG.load(fs);
            String projectHome = CONFIG.getProperty(Constants.PROJECT_HOME);
            String buildName = CONFIG.getProperty("TEST_LINK_BUILD");
            if (null != targetBuildName) {
            	buildName = targetBuildName;
			}
            String path = CONFIG.getProperty("screenshotPath");
            String reportXlsFile = prepareConsolidatedReportStart(buildName, path, projectHome);
            
            Xls_Reader seqFileReader = new Xls_Reader(System.getProperty("user.dir") + File.separatorChar + "config"
                            + File.separatorChar + projectHome + File.separatorChar + "Sequential_Test_Config.xls");

            int rowCount = seqFileReader.getRowCount(Constants.SHEET_NAME_SEQUENCE);
            setSuiteStartTimeForReports();
            for (int i = 2; i <= rowCount; i++) {
            	
            	 String functionality = seqFileReader.getCellData(Constants.SHEET_NAME_SEQUENCE,
                         Constants.FUNCTIONALITY_NAME, i);
            	 String jiraReportName = seqFileReader.getCellData(Constants.SHEET_NAME_SEQUENCE,
                 		Constants.JIRA_PROJECT_NAME, i);
            	 
            	
    	        
               
                
                String executeTest = seqFileReader
                                .getCellData(Constants.SHEET_NAME_SEQUENCE, Constants.EXECUTE_TEST, i);

                if (Constants.BOOLEAN_YES.equals(executeTest)) {
                	String jiraProjectFileName = path + buildName + File.separatorChar + functionality + ".xml";
                	File file = new File(jiraProjectFileName);
                	String startTime = TestUtil.now("dd.MMMMM.yyyy hh.mm.ss aaa");
        	        ReportUtil.startJiraXml(file, functionality, jiraReportName);
        	        
                    String testBrowser = seqFileReader.getCellData(Constants.SHEET_NAME_SEQUENCE,
                                    Constants.TEST_BROWSER, i);
                    System.setProperty(Constants.FUNCTIONALITY_NAME, functionality);
                    System.setProperty(Constants.TEST_BROWSER, testBrowser);
                    System.setProperty("FINAL_REPORT_PATH", reportXlsFile);
                    System.setProperty(Constants.JIRA_PROJECT_NAME, jiraReportName);
                    System.setProperty("JIRA_FUNC_XML", jiraProjectFileName);
                    JUnitCore.runClasses(DriverScript.class);
                    
                    ReportUtil.endJiraStatus(file);
                    String jiraProject = System.getProperty(Constants.JIRA_PROJECT_NAME);
                    ReportUtil.executeJiraStatus(file, startTime, jiraProject);
                }
            }
            prepareConsolidatedReportEnd(reportXlsFile, CONFIG, buildName);
        } catch (Exception e) {
            if (LOG.isDebugEnabled()) {
                LOG.error("SequentialTestSuiteRunner.main - Error - ", e);
            } else {
                LOG.error("SequentialTestSuiteRunner.main - Error - " + e);
            }

        }
        if (LOG.isDebugEnabled()) {
            LOG.debug("SequentialTestSuitRunner.main - end");
        }
        //System.exit(0);
    }

	private static void setSuiteStartTimeForReports() {
		String reportFilterStartTime = TestUtil.now("dd/MM/yyyy HH:mm:ss");
		System.setProperty("REPORT_START_TIME", reportFilterStartTime);
	}

	private static void prepareConsolidatedReportEnd(String reportXlsFile, Properties CONFIG, String buildName) throws IOException, MessagingException {
		FileInputStream file = new FileInputStream(new File(reportXlsFile));
		//Get the workbook instance for XLS file 
		HSSFWorkbook workbook = new HSSFWorkbook(file);
		printTestSuiteReport(workbook, CONFIG, buildName);
		sendEmail(CONFIG, buildName);
	}

	private static void sendEmail(Properties CONFIG, String buildName) throws MessagingException, IOException {
        String emailRecipient = CONFIG.getProperty("EXTERNAL_EMAIL_RECIPIENT");
        String sendEmailFlag = CONFIG.getProperty(Constants.SEND_EMAIL);
        String path = CONFIG.getProperty("screenshotPath");
        String projectHome = CONFIG.getProperty(Constants.PROJECT_HOME);
        String tomcatBaseURL = CONFIG.getProperty("TOMCAT_BASE_URL");
        if (null != sendEmailFlag && "Y".equals(sendEmailFlag)) {
        	List<String> recipients = new ArrayList<String>();
			if (null != emailRecipient && !"".equals(emailRecipient)) {
				recipients = Arrays.asList(emailRecipient.split("\\s*,\\s*"));
			} else {
				recipients.add("milind.warade@kpit.com");
//				recipients.add("nayana.kumbhar@kpit.com");
//				recipients.add("Neelima.Dwivedi@kpit.com");
			}
						
			SendMail.sendSeleniumConsolidatedEmail(path, buildName, projectHome, recipients, tomcatBaseURL);
		}
		
	}
	/**
	 * @param workbook
	 * @param buildName 
	 * @throws IOException
	 */
	private static void printTestSuiteReport(HSSFWorkbook workbook, Properties CONFIG, String buildName) throws IOException {
		String projectHome = CONFIG.getProperty(Constants.PROJECT_HOME);
		String path = CONFIG.getProperty("screenshotPath");
		String tomcatBaseURL = CONFIG.getProperty("TOMCAT_BASE_URL");
		StringBuilder reportBuilder = new StringBuilder();
		reportBuilder.append("<HTML>");
		reportBuilder.append("<img src='data:image/jpeg;base64,' width='1200' height='100' alt='base64 test'>");
		reportBuilder.append("<HEAD>");
        reportBuilder.append("<TITLE>Selenium Automation Consolidated Test Results</TITLE>");
        reportBuilder.append("</HEAD>");
		reportBuilder.append("<BODY>");
		reportBuilder.append("<h4 align=center><FONT COLOR=660066 FACE=AriaL SIZE=6><b><u> Selenium Automation Execution Results</u></b></FONT></h4>\n");
		//number of testSuites
		int numberOfTestCases = 0;
	 	int passedTestCases = 0;
		int failedTestCases = 0;
		int notRunTestCases = 0;
		String testSuiteName = "";
		String startTime = "";
		String endTime = "";
		int numberOfSheets = workbook.getNumberOfSheets();
		StringBuilder mainTableBuilder = new StringBuilder();
		for (int i = 0; i < numberOfSheets; i++) {
		 	HSSFSheet hssfSheet = workbook.getSheetAt(i);
		 	testSuiteName = testSuiteName + "," + hssfSheet.getSheetName();
		 	numberOfTestCases += hssfSheet.getLastRowNum()+1;
		 	
		 	if (i == 0) {
				HSSFRow firstRow = hssfSheet.getRow(0);
				HSSFCell startTimeCell = firstRow.getCell(3);
				startTime = startTimeCell.getStringCellValue();
			}
			HSSFRow lastRow = hssfSheet.getRow(hssfSheet.getLastRowNum());
		 	HSSFCell endTimeCell = lastRow.getCell(4);
		 	endTime = endTimeCell.getStringCellValue();
	 		
			mainTableBuilder.append("<h4> <FONT COLOR=660000 FACE=Arial SIZE=4.5> <u> Details for Test Suite - "+ hssfSheet.getSheetName() +" :</u></FONT></h4>\n");
			mainTableBuilder.append("<TABLE border=1 cellspacing=1 cellpadding=1 width=100% >");
			mainTableBuilder.append("<TR>");
			mainTableBuilder.append("<TD  width=5%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Test Script# </FONT></TD>");
			mainTableBuilder.append("<TD  width=35%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Test Case Name </FONT> </TD>");
			mainTableBuilder.append("<TD  width=5%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Status </FONT></TD>");
			mainTableBuilder.append("<TD  width=5%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Total Steps </FONT></TD>");
			mainTableBuilder.append("<TD  width=5%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Passed Steps </FONT></TD>");
			mainTableBuilder.append("<TD  width=5%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Failed Steps </FONT></TD>");
			mainTableBuilder.append("<TD  width=20%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Run Start Time </FONT></TD>");
			mainTableBuilder.append("<TD  width=20%  align= center  bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE= Arial  SIZE=2> Run End Time </FONT></TD>");
			mainTableBuilder.append("</TR>");
			int count = 0;
		 	for (int j = 0; j < numberOfTestCases; j++) {
		 		HSSFRow row = hssfSheet.getRow(j);
		 		if (null != row) {
					try {
						HSSFCell testCaseNameCell = row.getCell(2);
						HSSFCell startTimeEachCell = row.getCell(3);
						HSSFCell endTimeEachCell = row.getCell(4);
						HSSFCell statusCell = row.getCell(5);
						String status = statusCell.getStringCellValue();
						HSSFCell totalStepsCell = row.getCell(7);
						HSSFCell passedStepsCell = row.getCell(8);
						HSSFCell failedStepsCell = row.getCell(9);
						HSSFCell testStepsFolderPathCell = row.getCell(6);
						String testStepsFolderPath = testStepsFolderPathCell
								.getStringCellValue();
						mainTableBuilder.append("<TR>");
						mainTableBuilder
								.append("<TD width=5%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ count++ + "</FONT</TD>");
						mainTableBuilder
								.append("<TD  width=35%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2><b><a href="
										+ tomcatBaseURL
										+ testStepsFolderPath
										+ ".html>"
										+ testCaseNameCell.getStringCellValue()
										+ "</a></b></FONT</TD>");
						if ("Pass".equals(status)) {
							mainTableBuilder
									.append("<TD  width=5%  align= center bgcolor=#BCE954><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
											+ status + "</FONT</TD>");
							passedTestCases++;
						} else if ("Skip".equals(status)) {
							mainTableBuilder
									.append("<TD  width=5%  align= center bgcolor=Grey><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
											+ status + "</FONT</TD>");
							notRunTestCases++;
						} else {
							mainTableBuilder
									.append("<TD  width=5%  align= center bgcolor=Red><FONT COLOR=#153E7E FACE= Arial  SIZE=2>Fail</FONT</TD>");
							failedTestCases++;
						}
						mainTableBuilder
								.append("<TD  width=5%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ totalStepsCell.getNumericCellValue()
										+ "</FONT></TD>");
						mainTableBuilder
								.append("<TD  width=5%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ passedStepsCell.getNumericCellValue()
										+ "</FONT></TD>");
						mainTableBuilder
								.append("<TD  width=5%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ failedStepsCell.getNumericCellValue()
										+ "</FONT></TD>");
						mainTableBuilder
								.append("<TD  width=20%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ startTimeEachCell.getStringCellValue()
										+ "</FONT></TD>");
						mainTableBuilder
								.append("<TD  width=20%  align= center><FONT COLOR=#153E7E FACE= Arial  SIZE=2>"
										+ endTimeEachCell.getStringCellValue()
										+ "</FONT></TD>");
						mainTableBuilder.append("</TR>");
					} catch (Exception e) {
						System.err.println("Ignoring row - " + j + " from sheet " + hssfSheet.getSheetName());
					}
				}
			}
		 	mainTableBuilder.append("</TABLE>");
		 	
		 	
		}
		StringBuilder summaryTableBuilder = new StringBuilder();
		summaryTableBuilder.append("<h4> <FONT COLOR=660000 FACE=Arial SIZE=4.5> <u> Summary :</u></FONT></h4>\n");
		summaryTableBuilder.append("<table  border=1 cellspacing=1 cellpadding=1 >\n");
		
			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Project Name</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + CONFIG.getProperty(Constants.PROJECT_HOME) + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Test Suite Name</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + testSuiteName + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");
			
			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>TestLink Build</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + buildName + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Total TestCases</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + numberOfTestCases + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Pass TestCases</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + passedTestCases + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Fail TestCases</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + failedTestCases + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Not Run TestCases</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + notRunTestCases + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");

			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>Start Time</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + startTime + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");
			
			summaryTableBuilder.append("<TR>");
			summaryTableBuilder.append("<td width=150 align=left bgcolor=#153E7E><FONT COLOR=#E0E0E0 FACE=Arial SIZE=2.75><b>End Time</b></FONT></td>");
			summaryTableBuilder.append("<td width=150 align=left><FONT COLOR=#153E7E FACE=Arial SIZE=2.75><b>" + endTime + "</b></FONT></td>");
			summaryTableBuilder.append("</TR>");
		
		summaryTableBuilder.append("</table>");
		
		reportBuilder.append(summaryTableBuilder);
		reportBuilder.append(mainTableBuilder);
		reportBuilder.append("</BODY>");
		reportBuilder.append("</HTML>");
		
		
		File file = new File(path + buildName);
        file.mkdirs();
		BufferedWriter bwr = new BufferedWriter(new FileWriter(new File(path + buildName + File.separator + projectHome + "_SeleniumConsolidatedResult.html")));
		bwr.write(reportBuilder.toString());
		bwr.flush();
		bwr.close();
	}

	private static String prepareConsolidatedReportStart(String buildName,
			String path, String projectHome) throws IOException {
		File folder = new File(path + buildName);
		folder.mkdirs();
		HSSFWorkbook workbook = new HSSFWorkbook();
		String pathname = folder + File.separator + projectHome + "_result.xls";
		FileOutputStream out = 
			new FileOutputStream(new File(pathname));
		workbook.write(out);
		out.close();
		return pathname;
	}
}
